// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

contract SignatureVerifier {

    // Event to emit when a signature is verified
    event SignatureVerified(
        address indexed signer,
        bytes32 indexed assetHash,
        bool isValid
    );

    // Function to verify the signature of an asset hash
    function verifySignature(
        bytes32 assetHash,
        bytes memory signature,
        address expectedSigner
    ) public returns (bool) {
        // Recover the signer from the signature
        address recoveredSigner = recoverSigner(assetHash, signature);

        // Check if the recovered signer matches the expected signer
        bool isValid = recoveredSigner == expectedSigner;

        // Emit an event with the verification result
        emit SignatureVerified(recoveredSigner, assetHash, isValid);

        return isValid;
    }

    // Helper function to recover the signer from a signature
    function recoverSigner(bytes32 assetHash, bytes memory signature) internal pure returns (address) {
        // Ensure the signature length is 65 bytes (r, s, v)
        require(signature.length == 65, "Invalid signature length");

        // Split the signature into r, s, and v
        bytes32 r;
        bytes32 s;
        uint8 v;

        // Extract r, s, v from the signature
        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 65)))
        }

        // Adjust v to be 27 or 28 (Ethereum standard)
        if (v < 27) {
            v += 27;
        }

        // Ensure v is valid
        require(v == 27 || v == 28, "Invalid recovery id");

        // Hash the message with Ethereum's signed message prefix
        bytes32 prefixedHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", assetHash)
        );

        // Recover the signer address
        return ecrecover(prefixedHash, v, r, s);
    }

    // Optional: Utility function to compute the asset hash from metadata
    function computeAssetHash(string memory assetJson) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(assetJson));
    }
}

